package com.activitiserver.feignclient;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;@FeignClient(value = "USER-SERVER", path = "/user")public interface UserService {    /**     * 根据 员工号 查询员工信息     * @param empNo     * @return     */    @RequestMapping(value = "/empNo/{empNo}", method = RequestMethod.GET)    String findByEmpNo(@PathVariable(required = false) String empNo);    /**     * 根据 角色名称 查用户信息     * @param roleName     * @return     */    @RequestMapping(value = "/roleName/{roleName}", method = RequestMethod.GET)    String findByRoleName(@PathVariable(required = false) String roleName);}