package com.doctor.assistant.commonserver.entity;import lombok.Data;import java.io.Serializable;import java.util.Arrays;import java.util.List;@Datapublic class User implements Serializable {    public User() {    }    public User(String userName, int age) {        this.userName = userName;        this.age = age;    }    public User(String userName, int age, String info) {        this.userName = userName;        this.age = age;        this.info = info;    }    private String userName;    private int age;    private String info;    public static List<User> userListA = Arrays.asList(            new User("关羽", 666),            new User("赵云", 666),            new User("张飞", 666),            new User("刘备", 666));    public static List<User> userListB = Arrays.asList(            new User("关羽", 999, "武财神"),            new User("关云长", 999, "忠义"),            new User("关公", 999, "汉寿亭侯"),            new User("赵子龙", 999, "永昌亭侯"));}