package com.doctor.assistant.scheduleserver.controller;import com.doctor.assistant.scheduleserver.entity.City;import com.doctor.assistant.scheduleserver.server.CityDataService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;//用于处理rest请求的controller@RestController@RequestMapping("/cities")public class CityController {	@Autowired	private CityDataService cityDataService;	@GetMapping("/list")	public List<City> listCity() throws Exception {		return cityDataService.listCity();	}	}